import React, { useState, useEffect } from 'react';

import { Container } from './style';

import api from '../../services/api';

interface Iproduct{
    id: number;
    photo: string;
    name: string;
    description: string;
    price: number;
}


const Home: React.FC = () => {
    const [ data, setData ] = useState<Iproduct[]>([]);
    const [ cart, setCart ] = useState<Iproduct>({} as ICart);
    useEffect (() =>{
        api.get('').then(
            response => {
                setData(response.data)
            }
        )
    }, [])
    
    const handleCart = (index:number) => {
        let product = data[index]
        let shopcart = { ... cart, cart: product}
        setCart(shopcart)
        console.log(shopcart)

    }

    const handleCartStorage = (index:number) => {
        const productStore = JSON.stringify(data[index]);
        localStorage.setItem(`@produto-${index}`, productStore)   
    }

    return(
      <Container>
         <section>
             {data.map( (prod, index) =>(
               
                <div className="product-content" key={prod.id}>
                    <img src={prod.photo} alt="cursos" width="200" height="auto"/>
                    <h4>{prod.name}</h4>
                    <span>{prod.description}</span>
                    <h6>{prod.price}</h6>
                    <button onClick= {() => handleCart(index)}>Adicionar ao carrinho</button>
                </div>  
             ))}
          
         </section>
          </Container>
  );
}

export default Home;
