import React, { useState, useEffect } from 'react';

import { Container } from './style';

import api from '../../services/api';

interface Iproduct{
    id: number;
    photo: string;
    name: string;
    description: string;
    price: number;
}
const Home: React.FC = () => {
    const [ data, setData ] = useState<Iproduct[]>([]);
    useEffect (() =>{
        api.get('').then(
            response => {
                setData(response.data)
            }
        )
    }, [])
    
    const handleCart = (Index) => {
      
