    }

    return(
      <Container>
         <section>
             {data.map( prod =>(
               
                <div className="product-content" key={prod.id}>
                    <img src={prod.photo} alt="cursos" width="200" height="auto"/>
                    <h4>{prod.name}</h4>
                    <span>{prod.description}</span>
                    <h6>{prod.price}</h6>
                    <button>Adicionar ao carrinho</button>
                </div>  
             ))}
          
         </section>
          </Container>
  );
}

export default Home;
